mapper.update:
UPDATE REQUEST 
SET REQUESTSTATUS=#{requestStatus}
WHERE REQUESTNO=#{requestNo}

UPDATE MEMBER 
SET MEMBERSTATUS='N'
WHERE MEMBERID=#{memberId}

UPDATE MEMBER 
SET GRADECODE='gold'
WHERE MEMBERID=#{memberId}

UPDATE MEMBER 
SET GRADECODE='vip'
WHERE MEMBERID=#{memberId}

UPDATE MEMBER 
SET GRADECODE='mvg'
WHERE MEMBERID=#{memberId}

		UPDATE GRADE
        SET ACCRATE=#{accRate},
                GRADEBASE=#{gradeBase}
        WHERE GRADECODE=#{gradeCode}
