mapper.select:
SELECT * 
FROM CYCLE C 
JOIN (SELECT * FROM PRODUCT P JOIN MEDIUMCATE M ON P.MEDIUMCATE=M.MEDIUMCATE) PM 
ON C.PRODUCTNO=PM.PRODUCTNO 
WHERE MEMBERID=#{id} 

	AND LARGECATE=#{tab}


	AND MCNAME=#{detailTab}

mapper.update:
UPDATE CYCLE

	CYCLEMODE=#{chooseCycle},
	OFFCYCLE=#{offInput},
	LASTSTARTDATE=STARTDATE, STARTDATE=#{start}

WHERE MEMBERID=#{id} and PRODUCTNO=#{no}
