mapper.select:
SELECT * FROM CYCLE C JOIN (SELECT * FROM PRODUCT P JOIN MEDIUMCATE M ON P.MEDIUMCATE=M.MEDIUMCATE) PM ON C.PRODUCTNO=PM.PRODUCTNO JOIN (SELECT PRODUCTNO, ODAMOUNT FROM ORDERDETAIL WHERE ORDERDETAILNO IN ( SELECT MAX(OD.ORDERDETAILNO) FROM ORDER_TB OT JOIN ORDERDETAIL OD ON OT.ORDERNO=OD.ORDERNO WHERE OT.ORDERSTATUS IN ('a','b','c') GROUP BY OD.PRODUCTNO)) O ON C.PRODUCTNO=O.PRODUCTNO WHERE C.MEMBERID=#{id}
AND LARGECATE=#{tab}
AND MCNAME=#{detailTab}

mapper.update:
UPDATE CYCLE

	CYCLEMODE=#{chooseCycle},
	OFFCYCLE=#{offInput},
	LASTSTARTDATE=STARTDATE, STARTDATE=#{start}

WHERE MEMBERID=#{id} and PRODUCTNO=#{no}

UPDATE CYCLE 
SET LASTSTARTDATE=STARTDATE, STARTDATE=SYSDATE, ONCYCLE=#{onCycle}
WHERE MEMBERID=#{id} and PRODUCTNO=#{no}

mapper.select:
  		SELECT PRODUCTNO
		FROM BASKET
		WHERE BASKETNO=#{basketno} AND MEMBERID=#{id}
