mapper.update:
UPDATE WITHBOARD
SET WBDEL = 'N'
WHERE WBNO=#{wbNo}

UPDATE WITHBOARD 
SET WBCOUNT=WBCOUNT+1
WHERE WBNO=#{no}

UPDATE WITHBOARD
SET WBSTATUS = #{wbStatus}
WHERE WBNO = #{wbNo}

UPDATE WITHBOARD SET WBTITLE = #{wbTitle}, WBTYPE = #{wbType}, WBPRICE = #{wbPrice}, WBCONTENT = #{wbContent}, WBUSE = #{wbUse}, WBSHIP = #{wbShip, typeHandler=strArr}, WBPHONE =
#{wbPhone} DEFAULT
WHERE WBNO=#{wbNo}
